import { useState } from "react";


const bankLogos = {
  "HDFC Bank": "https://upload.wikimedia.org/wikipedia/en/thumb/a/a0/HDFC_logo.svg/1280px-HDFC_logo.svg.png",
  "ICICI Bank": "https://e7.pngegg.com/pngimages/229/146/png-clipart-logo-icici-bank-finance-bank-of-india-bank-text-logo.png", // shortened for brevity
  "Axis Bank": "https://upload.wikimedia.org/wikipedia/commons/thumb/1/1a/Axis_Bank_logo.svg/2560px-Axis_Bank_logo.svg.png", 
  SBI: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQpWvNVeGFA3OBzZ8nRuACZqki3iDkl4sY3gg&s",
  Kotak: "data:image/png;base64,",
  "Bank of Baroda": "https://e7.pngegg.com/pngimages/72/678/png-clipart-bank-of-baroda-logo-bank-logos-thumbnail.png",
  "Punjab National Bank": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRAJ188PE29OqwmP9PPIK76NMF_1DWIxmF75AOzBxh4Sya2QJJar8gsgJkuCeoA0EehYec&usqp=CAU",
  "Canara Bank": "https://kuvera.in/blog/wp-content/uploads/2023/01/Canara-Bank-Limited-Logo-300x167.jpg",
  "Union Bank": "data:image/png;base64,",
  "IDBI Bank": "https://www.logoshape.com/wp-content/uploads/2024/02/idbi-bank-vector-logo_logoshape.com_.png",
  "Yes Bank": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ7hIU7hKVcm98kSsxw_qhXR6-Xx2Wi-BSXAw&s",
   "Central Bank of India": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT7LnuvCtEIUmK1FXlm4CdOu3t5LmgpKkD8LQ&s",
  "IDFC First Bank": "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3f/Logo_of_IDFC_First_Bank.svg/1024px-Logo_of_IDFC_First_Bank.svg.png",
  "IndusInd Bank": "data:image/png;base64,",
};

const initialBanks = ["HDFC Bank", "ICICI Bank", "Axis Bank", "SBI", "Kotak"];

const extendedBanks = [
  "Bank of Baroda",
  "Punjab National Bank",
  "Canara Bank",
  "Union Bank",
  "IDBI Bank",
  "Yes Bank",
  "Central Bank of India",
  "IDFC First Bank",
  "IndusInd Bank",
];

export default function BankSelection() {
  const [banks, setBanks] = useState(initialBanks);
  const [showModal, setShowModal] = useState(false);

  const addBank = (bank) => {
    if (!banks.includes(bank)) {
      setBanks([...banks, bank]);
    }
    setShowModal(false); // Close modal after adding a bank
  };

  const handleNoBank = () => {
    setShowModal(false); // Close modal when user opts for no bank
  };

  return (
    <div className="min-h-screen bg-[#f3f0fa] p-10 w-full mx-auto">
      <h2 className="text-3xl font-semibold mb-10 text-[#2d1155] text-center">
        Select Your Bank
      </h2>
      <div className="grid grid-cols-3 gap-8 mb-8">
        {banks.map((bank, i) => (
          <div
            key={i}
            className="bg-white rounded-xl shadow hover:shadow-xl cursor-pointer p-8 flex flex-col items-center font-semibold text-[#5443cf] transition-transform transform hover:-translate-y-1"
          >
            {bankLogos[bank] ? (
              <img
                src={bankLogos[bank]}
                alt={bank}
                className="w-24 h-auto object-contain mb-4"
              />
            ) : (
              <div className="w-24 h-16 mb-4 bg-gray-300 flex items-center justify-center rounded-md text-gray-600">
                No Logo
              </div>
            )}
            {bank}
          </div>
        ))}
      </div>
      <button
        onClick={() => setShowModal(true)}
        className="block mx-auto w-60 py-4 rounded-full bg-[#5443cf] text-white font-semibold text-lg hover:bg-[#6d63fc] transition"
      >
        + Add New Bank
      </button>

      {showModal && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl p-8 max-w-4xl w-full overflow-y-auto max-h-[80vh] relative">
            <h3 className="text-2xl font-semibold mb-6 text-center text-[#5443cf]">
              Add Bank from List
            </h3>
            <button
              onClick={handleNoBank}
              className="absolute top-4 right-6 text-3xl font-bold text-gray-600 hover:text-gray-900"
              aria-label="Close modal"
              title="Close"
            >
              &times;
            </button>
            <div className="grid grid-cols-4 gap-6">
              {extendedBanks.map((bank, idx) => (
                <div
                  key={idx}
                  onClick={() => addBank(bank)}
                  className="cursor-pointer bg-[#f3f0fa] rounded-lg flex flex-col items-center p-4 hover:bg-[#e6e1fa] transition"
                >
                  {bankLogos[bank] ? (
                    <img
                      src={bankLogos[bank]}
                      alt={bank}
                      className="w-20 h-auto object-contain mb-3"
                    />
                  ) : (
                    <div className="w-20 h-12 bg-gray-300 rounded-md mb-3 flex items-center justify-center text-gray-600">
                      No Logo
                    </div>
                  )}
                  <span className="text-[#5443cf] font-medium text-center">{bank}</span>
                </div>
              ))}
            </div>
            <div className="mt-6 text-center">
              <button
                onClick={handleNoBank}
                className="inline-flex items-center px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 text-lg font-semibold focus:outline-none focus:ring-2 focus:ring-red-400"
              >
                &#x2716; No Bank/Back
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
